import React, {FC} from 'react'
import { RootState } from '../../../store/store'
import { useDispatch, useSelector } from 'react-redux'
import { emailChanOn, phoneChanOn } from '../../../store/historySlice'
import { useFindUser } from '../Form_Order/service-hook'

const FormHistory:FC = () => {

  const {findOneUser} = useFindUser()

  const email = useSelector((state:RootState) => state.history.email)
  const phone = useSelector((state:RootState) => state.history.phone)
  const dispatch = useDispatch()


  const emailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(emailChanOn(event.target.value))
  }

  const phoneChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(phoneChanOn(event.target.value))
  }


  const handle = async (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault()

    if (username === '') {
      return alert('Error write Name ')
    } else if (email === '') {
      return alert('Error write Email ')
    } else if (phone === '') {
      return alert('Error write Phone')
    } else if (address === '') {
      return alert('Error write Address')
    } else if (UserOrder.length === 0) {
      return alert('Error no data  Shopping Cart ')
    }
    if (await createUser({ username, email, phone, address })) {
      const { ...findUserEmail } : UserDataType[] = await findOneUser({ username, email, phone, address });
      if (findUserEmail) {
        for (let i = 0; i < UserOrder.length; i++) {
          await createOrder({ ...UserOrder[i], user_id:Number(findUserEmail[0].user_id) });
        }
      }
    }

    dispatch(nameChanOn(''))
    dispatch(emailChanOn(''))
    dispatch(phoneChanOn(''))
    dispatch(addressChanOn(''))
    dispatch(orderAllRemove())
  }


  return (

    <div className='border-b border-gray-900/10 pb-12 w-96 ml-12 mt-12'>
    <h2 className='text-base font-semibold leading-7 text-gray-900'>Personal Information</h2>
    <p className='mt-1 text-sm leading-6 text-gray-600'>
      Use a permanent address where you can receive mail.
    </p>

    <div className='mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6'>


      <div className='sm:col-span-4'>
        <label htmlFor='email' className='block text-sm font-medium leading-6 text-gray-900'>
          Email:
        </label>
        <div className='mt-2'>
          <input
           value={email}
           onChange={(event) =>emailChange(event)}
            id='email'
            name='email'
            type='email'
            autoComplete='email'
            className='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6'
          />
        </div>
      </div>

      <div className='sm:col-span-4'>
        <label htmlFor='email' className='block text-sm font-medium leading-6 text-gray-900'>
          Phone
        </label>
        <div className='mt-2'>
          <input
            value={phone}
            onChange={(event) => phoneChange(event)}
            id='phone'
            name='phone'
            type='phone'
            autoComplete='phone'
            className='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6'
          />
        </div>
      </div>


    </div>
    <div className='flex space-x-10 mt-10'>
      <button className='rounded-md bg-cyan-300 px-2.5 py-1.5  w-24 text-lg  text-gray-900 shadow-sm ring-1 ring-inset'>
        Submit
      </button>

    </div>



  </div>
  )
}

export default FormHistory
