import axios from 'axios'
import { User } from '../store/userSlice'
import { UserDataType } from '../types'

const URL_API = 'http://localhost:3004'

axios.defaults.baseURL = URL_API

export const userApi = {
  async postCreateUser(data: User) {
    const app = await axios.post('/user', data, {
      headers: { 'Content-Type': 'application/json' },
    })
    return app.data
  },

  async findUser(data: UserDataType) {
    const app = await axios.get<UserDataType[]>('/email', {
      params: data,
      headers: { 'Content-Type': 'application/json' },
    })

    return app.data
  },

  async findOneUser(email: string, phone: string) {
    const app = await axios.get<UserDataType[]>('/history', {
      params: { email, phone },
      headers: { 'Content-Type': 'application/json' },
    })

    return app.data
  },
}
