import { createSlice } from '@reduxjs/toolkit'

export interface CounterState {
  pizzaDay: boolean
  iqPizza: boolean
  kfc: boolean
}

const initialState: CounterState = {
  pizzaDay: true,
  IQPizza: false,
  KFC: false,
}

export const menuSlice = createSlice({
  name: 'menu',
  initialState,
  reducers: {
    PizzaDayOn: (state) => {
      state.PizzaDay = true
      state.IQPizza = false
      state.KFC = false
    },

    IQPizzaOn: (state) => {
      state.PizzaDay = false
      state.IQPizza = true
      state.KFC = false
    },

    KFCOn: (state) => {
      state.PizzaDay = false
      state.IQPizza = false
      state.KFC = true
    },
  },
})

export const { PizzaDayOn, IQPizzaOn, KFCOn } = menuSlice.actions
export default menuSlice.reducer
