import { PayloadAction, createSlice } from '@reduxjs/toolkit'


export interface User{
    username: string;
    email: string;
    phone: string;
    address: string
}

export interface CounterState {
    username: string;
    email: string;
    phone: string;
    address: string
}

const initialState: CounterState = {
    username:'',
    email: '',
    phone: '',
    address: ''
}



export const userSlice = createSlice({
  name: 'user',
  initialState,
  reducers: {


          nameChenOn:(state, action: PayloadAction<string>) => {

               state.username = action.payload


          },

          emailChenOn:(state, action: PayloadAction<string>) => {

                state.username = action.payload


            },

            phoneChenOn:(state, action: PayloadAction<string>) => {

                state.username = action.payload


                },

            addressChenOn:(state, action: PayloadAction<string>) => {

                state.username = action.payload


                    },

  },
})

// Action creators are generated for each case reducer function
export const { nameChenOn,   emailChenOn, phoneChenOn,  addressChenOn} = userSlice.actions

export default userSlice.reducer
