import React, { FC, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../store/store'
import { OrderDelete } from '../../store/orderSlice'

import ItemOrder from './ItemOrder'
import { setItem } from 'localforage'

const  ListOrder:FC = () => {


  const products =useSelector((state: RootState) => state.order.UserOrder)
  const dispatch = useDispatch()

  useEffect(() => {
    const order = JSON.parse(localStorage.getItem('order'));
    if (order) {
     setItems(order);
    }
  }, []);


  const deleteItem = (id:number):void => {
    dispatch(OrderDelete(id))
  }

  return (

    <div>


  <div className="bg-white">

  <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
    <h2 className="text-2xl font-bold tracking-tight text-gray-900">Customers also purchased</h2>

    <div className="mt-6 grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
      {products.map((product,id) => (

 <div>
        <ItemOrder key={product.id} product={product}/>
          <button type="button"
                className="rounded-md bg-red-300 px-2.5 py-1.5 text-sm  text-gray-900 shadow-sm ring-1 ring-inset "
                      onClick={() => deleteItem(product.id) } >
                        X
              </button>

      </div> 
      ))}

    </div>
  </div>
</div>
</div>
  )
}

export default ListOrder
