import { PayloadAction, createSlice } from '@reduxjs/toolkit'
import { Food } from '../type';


export interface Order{
  id: number,
  food_id: number;
  food_name: string;
  food_price: number;
  food_img: string;
}

export interface CounterState {
  id: number
  UserOrder : Order[]

}

const initialState: CounterState = {
  id:0,
  UserOrder: []
}



export const menuSlice = createSlice({
  name: 'stores',
  initialState,
  reducers: {
    PizzaDayOn: (state) => {
    state.PizzaDay = true
    state.IQPizza = false
    state.KFC = false


    },

    IQPizzaOn: (state) => {
      state.PizzaDay = false
      state.IQPizza = true
      state.KFC = false


      },


      KFCOn: (state) => {
        state.PizzaDay = false
        state.IQPizza = false
        state.KFC = true


        },


        OrderAdd: (state, action: PayloadAction<Food>) => {

          const newOrder = { ...action.payload, id: state.id++ };
          state.UserOrder.push(newOrder)


          },

          OrderDelete: (state, action: PayloadAction<number>) => {

            const foodIdToDelete = action.payload;

            const updatedUserOrder = state.UserOrder.filter(order => order.food_id !== foodIdToDelete);

            state.UserOrder = updatedUserOrder
            console.log(updatedUserOrder)

            },

  },
})

// Action creators are generated for each case reducer function
export const { PizzaDayOn , IQPizzaOn , KFCOn , OrderAdd ,OrderDelete} = menuSlice.actions

export default menuSlice.reducer
