import { useMutation } from "react-query"
import { User } from "../../store/userSlice"
import { userApi } from "../../service/userApi"
import { UserDataType } from "../../types"


export const useCreateUser = () => {
const { mutateAsync: createUser } = useMutation('user', (data: User) => userApi.postCreateUser(data), {
    onSuccess: () => {
      alert('User Saved Successfully')
    },

    onError: (error: any) => {
      alert(error)
    },
  })

  return { createUser }

}   



export const useFindUser = () => {

const { mutateAsync: findOneUser } = useMutation('user', (data: UserDataType) => userApi.findUser(data), {
    onSuccess: () => {

      alert('User Saved Successfully')
    },

    onError: (error: any) => {
      alert(error)

    },
  })
  return {findOneUser}
}
