import React, { FC } from 'react'
import { RootState } from '../../../store/store'
import { useSelector } from 'react-redux'
import Input from '../../UI/Input'
import { useFindAllOrder, useFindEmailUser, useLocalStorageService } from '../../../hooks'
import { userType } from '../../../types'
import { orderType } from '../../../types'
import { validateFormHistory } from '../../../helpers'

interface FormHistoryProps {
  emailChange: (event: any) => void
  phoneChange: (event: any) => void
}

const FormHistory: FC<FormHistoryProps> = ({ emailChange, phoneChange }) => {
  const { findEmailUser } = useFindEmailUser()
  const { findAllOrder } = useFindAllOrder()

  const email: string = useSelector((state: RootState) => state.history.email)
  const phone: string = useSelector((state: RootState) => state.history.phone)

  const handleFindUser = async (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault()

    const validator = validateFormHistory({ email, phone })

    if (validator) {

        const localStorageService = new useLocalStorageService()
        const {...order} = localStorageService.getItem('userOrder')
        console.log(order.food_id)



    }



  }
  return (
    <div className='border-b border-gray-900/10 pb-12 w-96 ml-12 mt-24 '>
      <div className='mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6'>
        <Input title='Email' value={email} onChange={(even) => emailChange(even)} />
        <Input title='Phone' value={phone} onChange={(even) => phoneChange(even)} />
      </div>
      <div className='flex space-x-10 mt-10'>
        <button
          className='rounded-md bg-cyan-300 px-2.5 py-1.5  w-24 text-lg  text-gray-900 shadow-sm ring-1 ring-inset'
          onClick={(event) => handleFindUser(event)}
        >
          Submit
        </button>
      </div>
    </div>
  )
}

export default FormHistory
