import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../store/store'
import { useMutation } from 'react-query'
import { userApi } from '../../service/userApi'
import { emailChanOn, nameChenOn, phoneChanOn } from '../../store/userSlice'

export default function Forma() {

  const OrderPrice = useSelector((state:RootState) => state.order.OrderPrice)



  // const postTodo = async (text: Todo['text']): Promise<Todo> => {
  //   const response = await fetch('api/tasks', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ text }),
  //   });
  //   if (!response.ok) {
  //     throw new ResponseError('Failed to insert new todo', response);
  //   }
  //   return await response.json();
  // };



  // const { mutate: addUser, isLoading, error } = useMutation(User, {
  //   onSuccess: () => {
  //     // Success actions
  //   },
  //   onError: (error) => {
  //     // Error actions
  //   },
  // });

  const username = useSelector((state:RootState) => state.user.username)
  const dispatch = useDispatch()


  const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
   dispatch(emailChanOn(event.target.value));
    console.log(event.target.value);
  };

  const handlePhoneChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(phoneChanOn(event.target.value));
