import React from 'react';

function App() {
  return (
    <div className="slider-navigation">
    {slides.map((slide, index) => (
      <div
        key={index}
        className={`slider-navigation-item ${index === activeSlide ? 'active' : ''}`}
        onClick={() => handleSlideChange(index)}
      >
        {index + 1}
      </div>
    ))}
  </div>
  );
}

export default App;
