import { createSlice } from '@reduxjs/toolkit'


interface Order{
  food_id: number;
  food_name: string;
  food_price: number;
  food_img: string;

}

export interface CounterState {
  PizzaDay: boolean,
  IQPizza: boolean,
  KFC: boolean,

}

const initialState: CounterState = {
  PizzaDay: true,
  IQPizza: false,
  KFC: false,
}

export const menuSlice = createSlice({
  name: 'stores',
  initialState,
  reducers: {
    PizzaDayOn: (state) => {
    state.PizzaDay = true
    state.IQPizza = false
    state.KFC = false


    },

    IQPizzaOn: (state) => {
      state.PizzaDay = false
      state.IQPizza = true
      state.KFC = false


      },


      KFCOn: (state) => {
        state.PizzaDay = false
        state.IQPizza = false
        state.KFC = true


        },

  },
})

// Action creators are generated for each case reducer function
export const { PizzaDayOn , IQPizzaOn , KFCOn } = menuSlice.actions

export default menuSlice.reducer
