import React, { FC } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RootState } from '../../../store/store'
import { UserDataType } from '../../../types'

import { validateFormaUser } from './validateFormaUser'
import Input from '../../UI/Input'




interface FormUserProps {
  nameChange: (event: React.ChangeEvent<HTMLInputElement>) => void
  emailChange: (event: React.ChangeEvent<HTMLInputElement>) => void
  phoneChange: (event: React.ChangeEvent<HTMLInputElement>) => void
  addressChange: (event: React.ChangeEvent<HTMLInputElement>) => void
  orderAllRemove: () => void
}

const FormUser: FC<FormUserProps> = ({nameChange, emailChange, phoneChange, addressChange, orderAllRemove}) => {


  const username = useSelector((state: RootState) => state.user.username)
  const email = useSelector((state: RootState) => state.user.email)
  const phone = useSelector((state: RootState) => state.user.phone)
  const address = useSelector((state: RootState) => state.user.address)
  const userOrder = useSelector((state: RootState) => state.order.userOrder)
  const orderPrice = useSelector((state: RootState) => state.order.orderPrice)

  // const nameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   dispatch(nameChanOn(event.target.value))
  // }

  // const emailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   dispatch(emailChanOn(event.target.value))
  // }

  // const phoneChange = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   dispatch(phoneChanOn(event.target.value))
  // }

  // const addressChange = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   dispatch(addressChanOn(event.target.value))
  // }

  const handleOrder = async (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault()

    const validate = validateFormaUser({username, phone, email, address})

     if(validate){

      const { ...user } = await createUser({ username, email, phone, address })

 if (user) {
      const { ...findUserEmail }: UserDataType[] = await findOneUser({
        username,
        email,
        phone,
        address,
      })


       if (findUserEmail) {
        for (let i = 0; i < userOrder.length; i++) {
          await createOrder({ ...userOrder[i], user_id: Number(findUserEmail[0].user_id) })
        }
      }

     }





    }


  }

  return (
    <div className='border-b border-gray-900/10 pb-12 w-96 ml-12 mt-12'>
      <h2 className='text-base font-semibold leading-7 text-gray-900'>Personal Information</h2>
      <p className='mt-1 text-sm leading-6 text-gray-600'>
        Use a permanent address where you can receive mail.
      </p>

      <div className='mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6'>

        <Input title='Name' value={username} onChange={nameChange}  />
        <Input title='Email' value={email} onChange={emailChange}  />
        <Input title='Phone' value={phone} onChange={phoneChange}  />
        <Input title='Address' value={address} onChange={addressChange}  />

      </div>
      <div className='flex space-x-10 mt-10'>
        <button
          className='rounded-md bg-cyan-300 px-2.5 py-1.5  w-24 text-lg  text-gray-900 shadow-sm ring-1 ring-inset'
          onClick={(event) => handleOrder(event)}
        >
          Submit
        </button>
        <h1 className='text-lg pt-2'>Price: {orderPrice} UA</h1>
      </div>
    </div>
  )
}
export default FormUser
