import { PayloadAction, createSlice } from '@reduxjs/toolkit'
import { Food } from '../type';



export interface Order{
  quantity: number
  food_id: number;
  food_name: string;
  food_price: number;
  food_img: string;
}

export interface CounterState {
  quantity: number
  OrderPrice: number
  UserOrder: Order[]
}

const initialState: CounterState = {
  quantity:0,
  OrderPrice: 0,
  UserOrder: []
}



export const menuSlice = createSlice({
  name: 'stores',
  initialState,
  reducers: {


          orderAdd:(state, action: PayloadAction<Food>) => {
          const newOrder = { ...action.payload};
          state.UserOrder.push({...newOrder , quantity:1})
          console.log(state.OrderPrice)
          state.OrderPrice = state.UserOrder.reduce((total,order) => total + Number(order.food_price), 0)
          },

          orderRemove:(state, action: PayloadAction<number>) => {

            const foodIdToDelete = action.payload;
            const updatedUserOrder = state.UserOrder.filter(order => order.food_id !== foodIdToDelete);
            state.UserOrder = updatedUserOrder
            state.OrderPrice = state.UserOrder.reduce((total,order) => total + Number(order.food_price), 0)


            },

            quantityAdd:(state, action: PayloadAction<number>) => {

              const foodId = action.payload;

         const order = state.UserOrder.find(() => o.food_id === foodId);

            //   state.UserOrder[action.payload].quantity++
            //  state.OrderPrice += state.UserOrder[action.payload].food_price
              },

              quantityLess:(state, action: PayloadAction<number>) => {
                state.UserOrder[action.payload].quantity--
                state.OrderPrice -= state.UserOrder[action.payload].food_price
                },

  },
})

// Action creators are generated for each case reducer function
export const {orderAdd ,orderRemove, quantityAdd,  quantityLess } = menuSlice.actions

export default menuSlice.reducer
