import axios from "axios";
import { ProductType } from "./type";

const URL_API = 'http://localhost:3001/post'

axios.defaults.baseURL = URL_API

export const ProductApi  = {
    async getAll(){
        return axios.get<ProductType[]>('/post')
    }
}
