import React, { FC } from 'react'

import ListProducts from '../components/Lists/ListProduct/ListProduct'
import { iqPizzaOn, kfcOn, pizzaDayOn } from '../store/menuSlice'
import { useDispatch, useSelector } from 'react-redux'
import Menu from '../components/MenuShop/Menu'
import { RootState } from '../store/store'

const Shop: FC = () => {

  const KFC = useSelector((state: RootState) => state.menu.kfc)
  const PizzaDay = useSelector((state: RootState) => state.menu.PizzaDay)
  const IQPizza = useSelector((state: RootState) => state.menu.IQPizza)

  const dispatch = useDispatch()
  return (
    <div>
      <div className='flex space-x-24 w-max '>
        <div className='mt-16'>
          <Menu 
            pizzaDayOn={dispatch(pizzaDayOn)}
            iqPizzaOn={dispatch(iqPizzaOn)}
            kfcOn={dispatch(kfcOn)} />
        </div>
      <ListProducts   />
      </div>
    </div>
  )
}

export default Shop
