import { createSlice } from '@reduxjs/toolkit'


export interface CounterState {
  PizzaDay: boolean,
  IQPizza: boolean,
  KFC: boolean,

}

const initialState: CounterState = {
  PizzaDay: true,
  IQPizza: false,
  KFC: false,
}

export const counterSlice = createSlice({
  name: 'stores',
  initialState,
  reducers: {
    PizzaGayOn: (state) => {
    state.PizzaDay = true
    state.IQPizza = false
    state.KFC = false
    state.Skincare = false

    },

    GroceriesOn: (state) => {
      state.Smartphones = false
      state.Groceries = true
      state.Laptops = false
      state.Skincare = false

      },

      LaptopOn: (state) => {
        state.Smartphones = false
        state.Groceries = false
        state.Laptops = true
        state.Skincare = false

        },

        SkincareOn: (state) => {
          state.Smartphones = false
          state.Groceries = false
          state.Laptops = false
          state.Skincare = true

          },




  },
})

// Action creators are generated for each case reducer function
export const { SkincareOn, GroceriesOn, LaptopOn, SmartphonesOn } = counterSlice.actions

export default counterSlice.reducer
