import { createSlice } from '@reduxjs/toolkit'

export interface nameStore {
  pizzaDay: boolean
  iqPizza: boolean
  kfc: boolean
}

const initialState: nameStore = {
  pizzaDay: true,
  iqPizza: false,
  kfc: false,
}

export const menuSlice = createSlice({
  name: 'menu',
  initialState,
  reducers: {
    pizzaDayOn: (state) => {
      state.pizzaDay = true
      state.iqPizza = false
      state.kfc = false
    },

    IQPizzaOn: (state) => {
      state.PizzaDay = false
      state.IQPizza = true
      state.KFC = false
    },

    KFCOn: (state) => {
      state.PizzaDay = false
      state.IQPizza = false
      state.KFC = true
    },
  },
})

export const { PizzaDayOn, IQPizzaOn, KFCOn } = menuSlice.actions
export default menuSlice.reducer
