import { PayloadAction, createSlice } from '@reduxjs/toolkit'
import { Food } from '../../type';



export interface Order{
  id: number,
  food_id: number;
  food_name: string;
  food_price: number;
  food_img: string;
}

export interface CounterState {
  id: number
  UserOrder : Order[]

}

const initialState: CounterState = {
  id:0,
  UserOrder: []
}



export const  orderAddSlice = createSlice({
  name: 'stores',
  initialState,
  reducers: {

        OrderAdd:(state, action: PayloadAction<Food>) => {

          const newOrder = { ...action.payload, id: state.id++ };
          state.UserOrder.push(newOrder)

          },



  },
})

// Action creators are generated for each case reducer function
export const {  OrderAdd } = orderAddSlice.actions

export default orderAddSlice.reducer
